mapper.select:
select id,title,resolve,state,level,week_date weekDate from problem


        and title like "%"#{title}"%"


        and week_date >= #{startDate}


        and week_date <= #{endDate}


        and state=#{state}


        and level=#{level}

    and user_id=#{userId}

order by state asc,week_date desc

select id, title, resolve, state, level, week_date weekDate
from problem
where week_date = #{weekDate} and user_id=#{userId}

select id, title, resolve, state, level, week_date weekDate
from problem
where id = #{id}

select id, title, resolve, state, level, week_date weekDate
from problem
where title = #{title}

select id, title, resolve, state, level, week_date weekDate
from problem
where title = #{name}
  and state = 0
  and user_id=#{userId}

mapper.update:
update problem
set state=1
where id = #{id}

update problem
set state=0
where id = #{id}
